import "./register.scss";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import authService from "../../services/auth";

const Register = () => {
   const [username, setUsername] = useState("");
   const [email, setEmail] = useState("");
   const [password, setPassword] = useState("");
   const [name, setName] = useState("");
   const [message, setMessage] = useState("");
   const [successful, setSuccessful] = useState(false);

   const onChangeUsername = (e) => {
      const USERNAME = e.target.value;
      setUsername(USERNAME);
      console.log("username check", USERNAME, username);
   };

   const onChangeEmail = (e) => {
      const EMAIL = e.target.value;
      setEmail(EMAIL);
      console.log("email check", EMAIL, email);
   };

   const onChangePassword = (e) => {
      const PASSWORD = e.target.value;
      setPassword(PASSWORD);
      console.log("password check", PASSWORD, password);
   };

   const onChangeName = (e) => {
      const NAME = e.target.value;
      setName(NAME);
      console.log("name check", NAME, name);
   };

   const handleRegister = (e) => {
      console.log("register working");
      e.preventDefault();
      authService
         .register(username, email, password, name)
         .then((res) => {
            setSuccessful(true);
            setMessage(res.data);
            console.log("pass check respon", res);

            // setTimeout(function () {
            //    window.location.reload();
            // }, 1000);
         })
         .catch((error) => {
            if (error.response) {
               setSuccessful(false);
               setMessage(error.response.data);
               console.log("failll error 1", error);
            } else if (error.request) {
               setSuccessful(false);
               setMessage(error.response);
               console.log("failll error 2", error);
            } else {
               setSuccessful(false);
               setMessage(error.response);
               console.log("failll error 3", error);
            }
         });

      // authService.register(username, email, password, name).then(
      //    (res) => {
      //       setSuccessful(true);
      //       setMessage(res.data);
      //       console.log("pass check respon", res);

      //       // setTimeout(function () {
      //       //    window.location.reload();
      //       // }, 1000);
      //    },
      //    (err) => {
      //       setSuccessful(false);
      //       setMessage(err.response.data);
      //       console.log("failll", err);
      //    }
      // );
   };

   return (
      <div className="register">
         <div className="card">
            <div className="left">
               <h1>UltraGo</h1>
               <p>Everything is out of reach until you reach it.</p>
               <span>Do you have an account?</span>
               <Link to="/login">
                  <button>Login</button>
               </Link>
            </div>
            <div className="right">
               <h1>Register</h1>
               <form>
                  <input
                     type="text"
                     placeholder="Username"
                     name="username"
                     onChange={onChangeUsername}
                  />
                  <input
                     type="email"
                     placeholder="Email"
                     name="email"
                     onChange={onChangeEmail}
                  />
                  <input
                     type="password"
                     placeholder="Password"
                     name="password"
                     onChange={onChangePassword}
                  />
                  <input
                     type="text"
                     placeholder="Name"
                     name="name"
                     onChange={onChangeName}
                  />
                  {/* {err && err} */}
                  <button onClick={handleRegister}>Register</button>
                  {message && (
                     <div className="message">
                        <div
                           className={
                              successful ? "alert-success" : "alert-fail"
                           }
                           role="alert"
                        >
                           {message}
                        </div>
                     </div>
                  )}
               </form>
            </div>
         </div>
      </div>
   );
};

export default Register;
