import React, { useState, useEffect } from "react";
import userService from "../../services/user";
import "./profile.css";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import Button from "@mui/material/Button";
import defaultUser from "../../images/defaultUser.png";

import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useParams } from "react-router-dom";
import authService from "../../services/auth";

const Profile = () => {
   window.scrollTo(0, 0);
   const [image, setImage] = useState("");
   const [fileName, setFileName] = useState("");
   const [data, setData] = useState([]);
   const [dataImage, setDataImage] = useState(false);
   const [currentUser, setCurrentUser] = useState("");
   const [open, setOpen] = useState(false);
   const [successfulChanged, setSuccessfulChanged] = useState(false);
   const [open2, setOpen2] = useState(false);

   const [email, setEmail] = useState("");
   const [password, setPassword] = useState("");
   const [confirmpassword, setConfirmPassword] = useState("");
   const [name, setName] = useState("");
   const [passwordcheck, setPasswordcheck] = useState(false);
   const [emailcheck, setEmailcheck] = useState(true);

   const [open3, setOpen3] = useState(false);
   const [matched, setMatched] = useState(false);

   const { username } = useParams();
   // const navigate = useNavigate();

   useEffect(() => {
      // const user = authService.getCurrentUser();
      // if (user) {
      //    // console.log("setttt");
      //    setCurrentUser(user);
      // }
      getUserProfile(username);
      getUserPic(username);
      testingUser(username);
   }, [username]);

   console.log("username check...", username);
   console.log("current user check...", currentUser);

   const testingUser = (username) => {
      userService
         .gettestinguser()
         .then((res) => {
            console.log("testing user check in profile.js", res);
         })
         .catch((err) => {
            console.log("testing user error", err);
         });
   };

   const getUserProfile = (username) => {
      console.log("checkkkkkkkkkuser namemememe", username);
      userService
         .getUser()
         .then((res) => {
            console.log("check getuser res...", res);
            setCurrentUser(res.data);
         })
         .catch((err) => {
            console.log("errrororr...", err);
            // if (err.response.status === 410) {
            //    console.log("failll error 410", err.response.data);
            //    navigate("/");
            // } else {
            //    console.log("failll error...", err);
            // }
         });
   };

   const getUserPic = (username) => {
      userService
         .getUserPic(username)
         .then((res) => {
            console.log("ressss check....", res);
            setData(res.data);
            setDataImage(true);
         })
         .catch((err) => {
            console.log(err);
         });
   };

   console.log("check dataaaaaa", data);

   const saveFile = (e) => {
      if (!e.target.files[0]) {
         setImage("");
         setFileName("");
      } else {
         setImage(e.target.files[0]);
         setFileName(e.target.files[0].name);
      }
   };

   const handleOpen = () => {
      setOpen(true);
   };

   const handleClose = () => {
      if (successfulChanged === true) {
         setOpen(false);
         setImage("");
         setFileName("");
         window.location.reload();
      } else {
         setImage("");
         setFileName("");
         setOpen(false);
      }
   };

   const changeFile = (e) => {
      const formData = new FormData();
      formData.append("image", image);
      formData.append("fileName", fileName);
      formData.append("username", username);
      console.log("inside..form...", formData);
      if (image || fileName !== "") {
         axios
            .post("http://localhost:8000/api/upload", formData)
            .then((res) => {
               if (res.data.Status === "Success") {
                  console.log("Succeded");
                  setSuccessfulChanged(true);
                  setOpen(false);
                  window.location.reload();
               } else {
                  console.log("Failed", res);
               }
            })
            .catch((err) => {
               console.log(err);
            });
      } else {
         toast.info("No changes");
         setOpen(false);
      }
   };

   const handleOpen2 = () => {
      setOpen2(true);
      setName(currentUser.name);
      setEmail(currentUser.email);
   };

   const handleClose2 = () => {
      setOpen2(false);
   };

   const onChangeEmail = (e) => {
      const check = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
      const ValidationEmail = check.test(e.target.value);
      console.log("curretn email check", currentUser.email);
      if (ValidationEmail === false) {
         setEmailcheck(false);
      } else {
         setEmailcheck(true);
         const EMAIL = e.target.value;
         setEmail(EMAIL);
         console.log("email check", EMAIL, email);
      }
   };

   const onChangePassword = (e) => {
      const check =
         /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{6,10}$/;
      const ValidationPassword = check.test(e.target.value);
      console.log("curretn password check", currentUser.password);
      const PASSWORD = e.target.value;
      setPassword(PASSWORD);

      if (PASSWORD !== confirmpassword) {
         setMatched(false);
      } else {
         if (ValidationPassword === false) {
            setPasswordcheck(false);
            console.log("error password..", password, e.target.value);
         } else {
            setPasswordcheck(true);
            console.log("password check", PASSWORD, password);
         }
      }
   };

   const onChangeConfirmPassword = (e) => {
      const check =
         /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{6,10}$/;
      const ValidationPassword = check.test(e.target.value);
      console.log("curretn password check", currentUser.password);
      const PASSWORD = e.target.value;

      if (PASSWORD !== password) {
         setMatched(false);
         console.log("password nototootot match", password, PASSWORD);
      } else {
         setMatched(true);
         console.log("matchhchhchchc", password, PASSWORD);
         if (ValidationPassword === false) {
            setPasswordcheck(false);
            console.log(
               "error confirm password..",
               confirmpassword,
               e.target.value
            );
         } else {
            setPasswordcheck(true);
            setConfirmPassword(PASSWORD);
         }
      }

      console.log("password check", PASSWORD, confirmpassword);
   };

   const onChangeName = (e) => {
      const NAME = e.target.value;
      setName(NAME);
      console.log("name check", NAME, name);
   };

   const changedProfile = async (e) => {
      e.preventDefault();
      console.log("check all...", email, name);
      if (emailcheck === false && email.length !== 0) {
         console.log("emaillllengthhhhhhhhh...", email.length);
         toast.error("The email is not in correct format");
      } else {
         await userService
            .editProfile(email, name, username)
            .then((res) => {
               toast.success(`${username} has been edited`);
               setOpen2(false);
               setTimeout(function () {
                  window.location.reload();
               }, 2000);
            })
            .catch((error) => {
               console.log("error backend...", error);
               if (error.response.status === 404) {
                  console.log("failll error 1", error.response.data);
                  toast.error("API Error");
               } else if (error.response.status === 409) {
                  console.log("failll error 2", error.response.data);
                  toast.error("ERROR 409");
               } else {
                  console.log("failll error 3", error);
                  toast.error("OTHER ERRORS");
               }
            });
      }
   };

   const handleOpen3 = () => {
      setOpen3(true);
   };

   const handleClose3 = () => {
      setOpen3(false);
   };

   const changedPassword = (e) => {
      e.preventDefault();
      console.log("check password", password, confirmpassword);

      if (matched === false) {
         console.log("matchhhh false", matched);
         toast.error("Password does not match");
      } else {
         if (passwordcheck === false) {
            toast.error(
               "The password must be 6 - 10 characters and contains one captial letter, one alphabet, one number and one special character."
            );
         } else {
            userService
               .changeUserPassword(confirmpassword, username)
               .then((res) => {
                  console.log("inside userService changeuserpassword");
                  toast.success(`Changed user:${username} password`);
                  setOpen3(false);
                  setTimeout(function () {
                     window.location.reload();
                  }, 2000);
               })
               .catch((error) => {
                  console.log("error backend...", error);
                  if (error.response.status === 404) {
                     console.log("failll error 1", error.response.data);
                     toast.error("API Error");
                  } else if (error.response.status === 409) {
                     console.log("failll error 2", error.response.data);
                     toast.error("ERROR 409");
                  } else {
                     console.log("failll error 3", error);
                     toast.error("OTHER ERRORS");
                  }
               });
         }
      }
   };

   return (
      <div className="Profile">
         <ToastContainer
            position="top-right"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme="light"
         />
         <h2 className="pageTitle">{username} Profile</h2>
         <br />
         <div className="imageContainer">
            {dataImage ? (
               <img
                  src={"http://localhost:8000/images/" + data.profilePic}
                  alt="ProfilePic"
                  className="profilePic"
               />
            ) : (
               <img src={defaultUser} alt="ProfilePic" className="profilePic" />
            )}
         </div>
         <br />
         <div className="profileContainer">
            <div className="profileInfo">
               <b>
                  <label className="labelProfile">Name: </label>
               </b>
               <br />
               <b>
                  <label className="labelProfile">Username: </label>
               </b>
               <br />
               <b>
                  <label className="labelProfile">Email: </label>
               </b>
               <br />
               <b>
                  <label className="labelProfile">Password: </label>
               </b>
            </div>
            <div className="profileInput">
               <input
                  className="inputProfile"
                  value={currentUser.name}
                  disabled
               ></input>
               <br />
               <input
                  className="inputProfile"
                  value={username}
                  disabled
               ></input>
               <br />
               <input
                  className="inputProfile"
                  value={currentUser.email}
                  disabled
               ></input>
               <br />
               <input
                  className="inputProfile"
                  value="**************"
                  disabled
               ></input>
               <br />
               <br />
            </div>
            <div className="bottomButtonDiv">
               <button className="changeImageButton" onClick={handleOpen}>
                  Change Picture
               </button>
               <Dialog open={open} onClose={handleClose}>
                  <DialogTitle className="changePicTitle">
                     Change Profile Picture
                  </DialogTitle>
                  <DialogContent>
                     <div
                        style={{
                           textAlign: "center",
                           marginTop: "10px",
                           marginBottom: "10px",
                        }}
                     >
                        {}
                        <img
                           src={
                              "http://localhost:8000/images/" + data.profilePic
                           }
                           alt="ProfilePic"
                           className="profilePicPopUp"
                        />
                     </div>
                     <div>
                        <input
                           type="file"
                           className="chooseFileInput"
                           onChange={saveFile}
                        />
                     </div>
                  </DialogContent>

                  <DialogActions>
                     <Button
                        onClick={changeFile}
                        style={{
                           cursor: "pointer",
                           marginRight: "20rem",
                        }}
                     >
                        Change Picture
                     </Button>
                     <Button
                        onClick={handleClose}
                        style={{ cursor: "pointer" }}
                     >
                        Cancel
                     </Button>
                  </DialogActions>
               </Dialog>
               <button className="editButton" onClick={handleOpen2}>
                  Edit Profile
               </button>
               <Dialog open={open2} onClose={handleClose2}>
                  <DialogTitle className="changePicTitle">
                     Edit Profile: <strong>{username}</strong>
                  </DialogTitle>
                  <DialogContent>
                     <div
                        style={{
                           marginTop: "30px",
                        }}
                     >
                        <div className="dialogInfo">
                           <strong>Name:</strong>
                           <br />
                           <br />
                           <strong>Email:</strong>
                           <br />
                        </div>
                        <div className="dialogInput">
                           <input
                              type="text"
                              placeholder={"Name"}
                              defaultValue={currentUser.name}
                              name="name"
                              onChange={onChangeName}
                              style={{
                                 fontFamily: "Verdana",
                                 marginBottom: "19px",
                                 paddingRight: "80px",
                              }}
                           ></input>
                           <br />

                           <input
                              type="email"
                              placeholder={"Email"}
                              defaultValue={currentUser.email}
                              name="email"
                              onChange={onChangeEmail}
                              style={{
                                 fontFamily: "Verdana",
                                 marginBottom: "8px",
                                 paddingRight: "80px",
                              }}
                           ></input>

                           <br />
                        </div>
                     </div>
                  </DialogContent>
                  <DialogActions>
                     <Button
                        onClick={changedProfile}
                        style={{
                           cursor: "pointer",
                           marginRight: "20rem",
                        }}
                     >
                        Save
                     </Button>
                     <Button
                        onClick={handleClose2}
                        style={{ cursor: "pointer" }}
                     >
                        Cancel
                     </Button>
                  </DialogActions>
               </Dialog>
               <button className="passwordButton" onClick={handleOpen3}>
                  Change Password
               </button>
               <Dialog open={open3} onClose={handleClose3}>
                  <DialogTitle className="changePicTitle">
                     Change Password
                  </DialogTitle>
                  <DialogContent>
                     <div
                        style={{
                           marginTop: "20px",
                        }}
                     >
                        <div className="dialog3Info">
                           <strong>New Password: </strong>
                           <br />
                           <br />
                           <br />
                           <strong>Confirm Password: </strong>
                        </div>
                        <div className="dialog3Input">
                           <input
                              style={{
                                 paddingRight: "60px",
                              }}
                              type="password"
                              name="password"
                              onChange={onChangePassword}
                           ></input>
                           <br />
                           <br />
                           <br />
                           <input
                              style={{
                                 paddingRight: "60px",
                              }}
                              type="password"
                              name="confirmpassword"
                              onChange={onChangeConfirmPassword}
                           ></input>
                        </div>
                     </div>
                  </DialogContent>
                  <DialogActions>
                     <Button
                        onClick={changedPassword}
                        style={{
                           cursor: "pointer",
                           marginRight: "20rem",
                        }}
                     >
                        Save
                     </Button>
                     <Button
                        onClick={handleClose3}
                        style={{ cursor: "pointer" }}
                     >
                        Cancel
                     </Button>
                  </DialogActions>
               </Dialog>
            </div>
         </div>
      </div>
   );
};

export default Profile;
