import React, { useState, useEffect } from "react";
import userService from "../../services/auth";
import authService from "../../services/auth";
import "./profile.css";

import axios from "axios";

import captain from "../../images/captain.jpg";

const Profile = () => {
   window.scrollTo(0, 0);
   const [image, setImage] = useState("");
   const [fileName, setFileName] = useState([]);

   useEffect(() => {
      const user = authService.getCurrentUser();

      if (user) {
         // console.log("setttt");
         setCurrentUser(user);
      }

      axios
         .get("http://localhost:8000/")
         .then((res) => {
            setFileName(res.data[1]);
         })
         .catch((err) => {
            console.log(err);
         });
   }, []);

   const saveFile = (e) => {
      setImage(e.target.files[0]);
      setFileName(e.target.files[0].name);
   };

   console.log("check filenamememe", fileName);
   const changeFile = async (e) => {
      const formData = new FormData();
      formData.append("image", image);
      formData.append("fileName", fileName);
      axios
         .post("http://localhost:8000/api/upload", formData)
         .then((res) => {
            if (res.data.Status === "Success") {
               console.log("Succeded");
            } else {
               console.log("Failed", res);
            }
         })
         .catch((err) => {
            console.log(err);
         });
      // try {
      //    userService.upload(formData).then((res) => {
      //       console.log(res);
      //    });
      //    console.log("success try");
      // } catch (ex) {
      //    console.log(ex);
      //    console.log("faillll");
      // }
   };

   const [currentUser, setCurrentUser] = useState("");

   return (
      <div className="Profile">
         <h2 className="pageTitle">{currentUser.username} Profile</h2>
         <br />
         <div className="profileContainer">
            {/* <div className="profilePic"> */}
            <img
               src={"http://localhost:8000/images/" + fileName}
               alt="captain"
               style={{ width: "1000px", height: "200px" }}
            />
            {/* </div> */}
            <input
               type="file"
               className="chooseFileInput"
               onChange={saveFile}
            />
            <button className="changePicButton" onClick={changeFile}>
               Change Profile Picture
            </button>
         </div>
         <br />
         <div className="profileContainer">
            <div className="profileInfo">
               <b>
                  <label className="labelProfile">Name: </label>
               </b>
               <br />
               <b>
                  <label className="labelProfile">Username: </label>
               </b>
               <br />
               <b>
                  <label className="labelProfile">Email: </label>
               </b>
            </div>
            <div className="profileInput">
               <input className="inputProfile"></input>
               <input className="inputProfile"></input>
               <input className="inputProfile"></input>
               <button className="editButton">Edit Profile</button>
            </div>
         </div>
         sss
      </div>
   );
};

export default Profile;
